def phone = context.expand( '${checkExt#Response#declare namespace ns10=\'http://www.yota.ru/vox/b2b/services/customerInformation\'; //ns10:GetCustomerByICCIDResponse[1]/customer[1]/CEO[1]/phones[1]/phoneNumber[1]}' )

